import React, { Component } from 'react';

export default class BlindTimer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            hidden: false,
            running: false,
        }

    }

    start() {
        const startTime = new Date();
        this.setState({ startTime, running: true, hidden: true });
    }

    stop() {
        const endTime = new Date();
        this.setState({ endTime, running: false, hidden: false});
    }

    reset() {
        this.setState({
            startTime: null,
            endTime: null,
        })
    }


    render() {
        return (
            <div>

            </div>
        )
    }
}
