import React, { Component } from 'react';

export default class Goal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            goal: this.props.goal,
            difficulty: this.props.difficulty,
            visbile: false
        }
    }

    renderGoalMessage() {
        const score = this.state.difficulty === 'HARD' ? this.props.score.split(',').pop() : this.props.score;
        if (this.state.goal === score && score) {

            return <h2 className="has-text-success">WIN</h2>
        } else if (this.state.goal !== score && score) {
            return <h2 className="has-text-danger">FAIL</h2>
        } else {
            return null;
        }

    }

    clearMessage() {
        setInterval(() => {
            this.setState({
                visible: false,
            })
        }, 2000);
    }

    render() {
        return (
            <div className="goal-message">
                {this.state.visible ? this.renderGoalMessage() : null}
            </div>
        )
    }
}
