import React, { Component } from 'react';

export default class BlindTimer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            hidden: false,
            running: false,
            time: '00.000',
            action: 'START',
            startTime: null,
            endTime: null,
        }        
    }

    start() {
        let startTime = (new Date()).getTime();
        console.log('START TIME = ', startTime);
        this.setState({ startTime, running: true, hidden: true, action: 'STOP' });
    }

    stop() {
        let endTime = (new Date()).getTime();
            console.log('END TIME = ', startTime);
        this.setState({ endTime, running: false, hidden: false, action: 'RESET' });
        this.formatTime();
    }

    reset() {
        this.setState({ startTime: null, endTime: null, action: 'START', time: '00.000' });
    }

    getDuration() {
        return this.state.endTime - this.state.startTime;
    } 

    formatTime() {
        const elapsedTime = this.getDuration();
        let ms = elapsedTime % 1000;
        console.log('ms = ', ms);
        if (ms < 10) ms = '00' + ms;
        else if (ms < 100) ms = '0' + ms;

        let s = Math.floor(elapsedTime / 1000);
        if (s < 10) s = '0' + s;

        let time = `${s}.${ms}`;

        this.setState({ time });
    }

