import React from 'react';

const Taunt = props => {
    const taunts = [
        'too slow',
        '4 mississippi',
        'stop!',
        'can you count?'
    ];

    let randomTaunt = '';

    const generateTaunt = taunts => { 
        setInterval(() => randomTaunt = taunts[Math.floor(Math.random() * taunts.length)], 2000);;
    }
    generateTaunt(taunts);

    return (
        <div className="column has-text-centered">
            <br/>
            <h2 className="title is-5 has-text-warning">
                { props.isRunning ? randomTaunt : null }
            </h2>
        </div>
    )
}

export default Taunt;
