import React, { Component } from 'react'

export default class Cue extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0,
            visualCues: this.props.visualCues
        }
    }

    incrementCount() {
        if (this.state.count < 10) {
            setInterval(() => {
                this.setState({count: this.state.count + 1});
            }, 1000)
        }
    }

    render() {
        console.log('HERE')
        this.incrementCount();
        return (
            <div className="column has-text-centered">
                <progress className="progress is-" value={`${this.state.count}%`} max="100">{this.state.count}%</progress>
            </div>
        )
    }
}
