import React from 'react';

const Taunt = props => {
    const taunts = [
        'too slow',
        '4 mississippi',
        'stop!',
        'can you count?'
    ];

    let T = '';
    const generateTaunt = taunts => { 
        const randomTaunt = () => random = taunts[Math.floor(Math.random() * taunts.length)];
        setInterval(randomTaunt(), 2000);
        return random;
    }

    return (
        <div className="column has-text-centered">
            <br/>
            <h2 className="title is-5 has-text-warning">
                { props.isRunning ? generateTaunt(taunts) : null }
            </h2>
        </div>
    )
}

export default Taunt;
