import React, { Component } from 'react';


export default class GoalMessage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            difficulty: '',
        }
    }

    handleDifficulty() {
        let score = this.state.difficulty === "IMPOSSIBLE" ? this.props.score : Math.floor(this.props.score);
        this.renderMessage(score);
    }

    renderMessage = score => {
        console.log('SCORE =', score);
        if (score === 10) {
            return <h2 className ="title is-5 has-text-success shake"> WIN </h2>
        } else if (this.state.goal !== score) {
            return <h2 className = "title is-5 has-text-danger shake"> FAIL </h2>
        }
    }

    render() {
        console.log('this.props.score = ', Math.floor(this.props.score));
        return (
            <div>
                {this.handleDifficulty()}
            </div>
        )
    }
}
