import React, {} from 'react';

export default class Taunt extends Component {
    constructor(props){
        super(props);
        this.state = {
            taunt: '',
            taunts: [
                'too slow',
                '4 mississippi',
                'stop!',
                'can you count?'
            ]
        }
    }

    generateRandomTaunt = () => {
        setInterval(() => {
            this.setState({
                taunt: this.state.taunts[Math.floor(Math.random() * taunts.length)]
            })
        }, 2000);
    }

    render(){
        return (
            <div className="column has-text-centered">
                <br/>
                <h2 className="title is-5 has-text-warning">
                    { props.isRunning ? generateRandomTaunt() : null }
                </h2>
            </div>
        )
    }


}
