import React from 'react';

const Taunt = () => {
    const taunts = [
        'too slow',
        '4 mississippi',
        'stop!',
        'can you count?'
    ];

    const generateTaunt = taunts => {
        setInterval(() => {
            return taunts[Math.floor(Math.random() * taunts.length)];
        }, 2000);
    }


    return (
        <div className="column has-text-centered">
            <h2 className="title is-5 has-text-warning">
                {generateTaunt(taunts)}
            </h2>
        </div>
    )
}

export default Taunt;
