import React, { Component } from 'react'

export default class Cue extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0,
            message: '',
            color: '',
            visualCues: this.props.visualCues
        }
    }

    incrementCount() {
        setInterval(() => {
            return this.state.count < 110 ? this.setState({count: this.state.count + 10}) : null;
        }, 1000)
    }

    listenColorChange() {
        setInterval(() => {
            this.handleColorChange();
        }, 1000);
    }

    handleColorChange() {
        let count = this.state.count;
        let color = '';
        let message = '';
        if (count <= 30) {
            color = 'progress is-danger';
            message = 'Keep going...';
        } else if (count > 30 && count <= 60) {
            color = 'progress is-warning';
            message = 'Almost...';
        } else if (count > 60 && count <= 90) {
            color = 'progress is-primary';
            message = 'Get ready...';
        } else if (count === 100) {
            color = 'progress is-success shake';
            message = 'NOW!';
        } else if (count > 100) {
            color = 'progress is-danger shake';
            message = 'Too slow ðŸ˜£';
        } else {
            color = 'progress is-dark';
            message = "Guide"
        }
        this.setState({ color, message })
    }

    componentDidMount() {
        this.incrementCount();
        this.listenColorChange();
    }

    render() {
        return (
            <div className="column has-text-centered">
                <br/>
                <h2 className="title has-text"></h2>
                <progress className={this.state.color} value={`${this.state.count}`} max="100">{`${this.state.count}%`}</progress>
            </div>
        )
    }
}
