import React, { Component } from 'react';

export default class BlindTimer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            hidden: false,
            running: false,
            time: '00.000',
            action: 'START'
        }        
    }

    componentDidMount() {
        this.handleTimeReveal();
    }

    start() {
        const startTime = new Date();
        this.setState({ startTime, running: true, hidden: true, action: 'STOP'});
    }

    stop() {
        const endTime = new Date();
        this.setState({ endTime, running: false, hidden: false, action: 'RESET'});
    }

    reset() {
        this.setState({ startTime: null, endTime: null, action: 'START' });
    }

    timeKeeper() {

    }

    handleTimeReveal() {

        this.start()

        setTimeout(() => {
            this.stop();
            console.log( this.state.endTime - this.state.startTime);
        }, 3000);
    }

    handleClick = () => {
        if (!this.state.running && this.state.action === 'START') {
            this.start();
        } else if (this.state.running &&  this.state.action === 'STOP') {
            this.stop();
        }
    }

    handleKeyDown= event => {
        if (event.keyCode === 32) {
            this.handleClick();
        }
    }

    render() {
        return (
            <div>
                <h2>{this.state.time}</h2>
                <button tabIndex={-1} onClick={() => this.handleClick()} onKeyDown={event => this.handleKeyDown(event)}>{this.state.action}</button>
            </div>
        )
    }
}
